document.addEventListener("DOMContentLoaded",function(){function initializeAWS(){console.log("AWS services would be initialized here in production")}const copyButtons=document.querySelectorAll(".btn-outline-secondary");if(copyButtons.length>0){copyButtons.forEach(button=>{if(button.previousElementSibling&&button.previousElementSibling.tagName==="INPUT"){button.addEventListener("click",function(){const inputElement=this.previousElementSibling;inputElement.select();document.execCommand("copy");const originalText=this.textContent;this.textContent="Copied!";setTimeout(()=>{this.textContent=originalText},2e3)})}})}function showToast(message,type="success"){let toastContainer=document.querySelector(".toast-container");if(!toastContainer){toastContainer=document.createElement("div");toastContainer.className="toast-container position-fixed bottom-0 end-0 p-3";document.body.appendChild(toastContainer)}const toastEl=document.createElement("div");toastEl.className=`toast align-items-center text-white bg-${type} border-0`;toastEl.setAttribute("role","alert");toastEl.setAttribute("aria-live","assertive");toastEl.setAttribute("aria-atomic","true");toastEl.innerHTML=`
            <div class="d-flex">
                <div class="toast-body">
                    ${message}
                </div>
                <button type="button" class="btn-close btn-close-white me-2 m-auto" data-bs-dismiss="toast" aria-label="Close"></button>
            </div>
        `;toastContainer.appendChild(toastEl);const toast=new bootstrap.Toast(toastEl);toast.show();toastEl.addEventListener("hidden.bs.toast",()=>{toastEl.remove()})}window.aiInterviewApp={initializeAWS:initializeAWS,showToast:showToast}});